mapper.select:
select t.id,t.title,t.test_type,t.gmt_create,t.gmt_modified,t.status,t.describtion,ifnull(i.interfaceCount,0) as interfaceCount,ifnull(c.caseCount,0)as caseCount from t_project as t left join (select count(*)as interfaceCount,project_id from t_interface group by project_id ) as i on t.id = i.project_id left join (select count(* ) as caseCount,project_id from t_interface_case group by project_id) as c on t.id = c.project_id
title like concat('%',#{p.title},'%')
and test_type = ${p.testType}
and gmt_create >#{p.begin}
and gmt_modified <#{p.end}
and status = 0
order by gmt_create desc
